import * as React from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
/**
 *
 * See the basic-react example for an example of adding and reacting to
 * changes in state and lecture 10 for details on Material-UI
 *
 * @param {object} props
 * @return {object} JSX
 *
 */
export default function ScrollDialog(props) {
  const [open, setOpen] = React.useState(false);
  const [scroll, setScroll] = React.useState('paper');
  // console.log(props.recipesList);
  console.log(props.recipeViewed);
  // console.log(props.recipeViewed.extendedIngredients);
  const ingredients = props.recipeViewed.extendedIngredients;
  const instructions = props.recipeViewed.analyzedInstructions;
  console.log(instructions);

  const handleClickOpen = (scrollType) => () => {
    setOpen(true);
    setScroll(scrollType);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const descriptionElementRef = React.useRef(null);
  React.useEffect(() => {
    if (open) {
      const {current: descriptionElement} = descriptionElementRef;
      if (descriptionElement !== null) {
        descriptionElement.focus();
      }
    }
  }, [open]);

  return (
    <div>
      <Button onClick={handleClickOpen('paper')}>scroll=paper</Button>
      <Button onClick={handleClickOpen('body')}>scroll=body</Button>
      <Dialog
        open={open}
        onClose={handleClose}
        scroll={scroll}
        aria-labelledby='scroll-dialog-title'
        aria-describedby='scroll-dialog-description'
      >
        <DialogTitle id='scroll-dialog-title'>Subscribe</DialogTitle>
        <DialogContent dividers={scroll === 'paper'}>
          <DialogContentText
            id='scroll-dialog-description'
            ref={descriptionElementRef}
            tabIndex={-1}
          >
            <Button onClick={() => props.setRecipeVisible(false)}>x</Button>
            <div className='recipe'>
              <div>{props.recipeViewed.title}</div>
              <div>Ingredients</div>
              {ingredients?.map((ingredient) => (
                <div>
                  <span
                    className='recipeContainer'
                    key={ingredient.name}
                  ></span>
                  <div> {ingredient.name}</div>
                </div>
              ))}
              {instructions?.map((instruction) => (
                <div>
                  <span
                    className='recipeContainer'
                    key={instruction.name}
                  ></span>
                  <div>Instructions</div>{' '}
                  {instruction.steps?.map((step) => (
                    <span className='recipeContainer' key={step.number}>
                      <span> {step.number}</span>
                      <span> {step.step}</span>
                    </span>
                  ))}
                </div>
              ))}
            </div>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleClose}>Subscribe</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
