// import React from 'react';
import * as React from 'react';
import {useState, useEffect} from 'react';
// import {useNavigate} from 'react-router-dom';
import axios from 'axios';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import MenuItem from '@mui/material/MenuItem';
import egyptianCuisine from './egypt.jpeg';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import PersonIcon from '@mui/icons-material/Person';
import './Home.css';

const pages = ['Dish', 'Cuisine', 'Ingredients', 'Type', 'Diet'];
/**
 * Simple component with no state.
 *
 * See the basic-react example for an example of adding and reacting to
 * changes in state and lecture 10 for details on Material-UI
 *
 * @return {object} JSX
 */
export default function Home() {
  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [recipesList, setRecipesList] = useState([]);
  // const navigate = useNavigate();

  // const navigateToCuisine = () => {
  //   navigate('/cuisine');
  // };

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  useEffect(() => {
    axios
      .get(`https://api.spoonacular.com/recipes/random`, {
        params: {apiKey: 'c33d1c92ed8343d7b6522ddb630cdfd4', number: 16},
      })
      .then((res) => {
        setRecipesList(res.data.recipes);
        console.log(res.data.recipes);
      });
  }, []);

  return (
    <div>
      {/* <h1 className='title'>AKALAT</h1> */}
      <AppBar style={{background: '#2E3B55'}} position='static'>
        <Container maxWidth='xl'>
          <Toolbar disableGutters>
            <Typography
              variant='h6'
              noWrap
              component='a'
              href=''
              sx={{
                mr: 2,
                display: {xs: 'none', md: 'flex'},
                fontFamily: 'fantasy',
                fontWeight: 700,
                letterSpacing: '.3rem',
                color: '#D88643',
                textDecoration: 'none',
              }}
            >
              AKALAT
            </Typography>

            <Box sx={{flexGrow: 1, display: {xs: 'flex', md: 'none'}}}>
              <IconButton
                size='large'
                aria-label='account of current user'
                aria-controls='menu-appbar'
                aria-haspopup='true'
                onClick={handleOpenNavMenu}
                color='white'
              >
                <MenuIcon />
              </IconButton>
              <Menu
                id='menu-appbar'
                anchorEl={anchorElNav}
                anchorOrigin={{
                  vertical: 'bottom',
                  horizontal: 'left',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'left',
                }}
                open={Boolean(anchorElNav)}
                onClose={handleCloseNavMenu}
                sx={{
                  display: {xs: 'block', md: 'none'},
                }}
              >
                {pages.map((page) => (
                  <MenuItem key={page} onClick={handleCloseNavMenu}>
                    <Typography href={`/${page}`} textAlign='center'>
                      {page}
                    </Typography>
                  </MenuItem>
                ))}
              </Menu>
            </Box>
            <Typography
              variant='h5'
              noWrap
              component='a'
              href=''
              sx={{
                mr: 2,
                display: {xs: 'flex', md: 'none'},
                flexGrow: 1,
                fontFamily: 'fantasy',
                fontWeight: 700,
                letterSpacing: '.3rem',
                color: '#D88643',
                textDecoration: 'none',
              }}
            >
              AKALAT
            </Typography>
            <Box
              sx={{
                flexGrow: 1,
                display: {xs: 'none', md: 'flex', fontFamily: 'fantasy'},
              }}
            >
              {pages.map((page) => (
                <Button
                  href={`/${page}`}
                  key={page}
                  onClick={handleCloseNavMenu}
                  sx={{
                    fontFamily: 'fantasy',
                    my: 2,
                    color: 'orange',
                    display: 'block',
                  }}
                >
                  {page}
                </Button>
              ))}
            </Box>
          </Toolbar>
        </Container>
      </AppBar>
      <div className='homePage'>
        <img className='egyptianCuisine' src={egyptianCuisine} alt='React' />
        <div className='homeIntro'>
          <img
            class='vertical-decorationtop'
            src='https://i.ibb.co/JRTK9z4/horizontally-centered-vertical-decoration.png'
            alt=''
          ></img>
          <h2 className='intro'>
            over 300,000 delicious recipes available from various cuisines
          </h2>
          <img
            class='vertical-decorationbottom'
            src='https://i.ibb.co/JRTK9z4/horizontally-centered-vertical-decoration.png'
            alt=''
          ></img>
        </div>
      </div>
      <h2 className='explore'>Explore Recipes</h2>
      <div className='recipeContainerList'>
        {recipesList.map((recipe) => (
          <span className='recipeContainer' key={recipe.title}>
            <img className='randImages' src={recipe.image} alt={'k'} />
            <span className='recipeTitle'>{recipe.title}</span>
            <span className='recipeServings'>
              <PersonIcon></PersonIcon> {recipe.servings}
            </span>
            <span className='recipeIngrNum'>
              <MenuBookIcon></MenuBookIcon>
              {recipe.extendedIngredients.length}
            </span>
            <span className='prepTime'>
              <AccessTimeIcon></AccessTimeIcon>
              {recipe.readyInMinutes}
            </span>
            <button className='viewRecipe'>View Recipe</button>
          </span>
        ))}
      </div>
    </div>
  );
}
