import React from 'react';
import './Picker.css';
/**
 * Picker component.
 */
class Picker extends React.Component {
  /**
   * Create a date picker
   * @param {string} containerId id of a node the Picker will be a child of
   */
  constructor(containerId) {
    super(containerId);
    if ((containerId) != null) {
      const currentDate = new Date();
      this.currentDate = currentDate;
      this.changedCurrentDate = currentDate;
      this.currentmonth = currentDate.getMonth();
      this.currentyear = currentDate.getFullYear();
      this.month = currentDate.getMonth();
      this.year = currentDate.getFullYear();
      this.months = ['January', 'February', 'March', 'April', 'May', 'June',
        'July', 'August', 'September', 'October', 'November', 'December'];
      this.setNext = this.setNext.bind(this);
      this.setPrev = this.setPrev.bind(this);
      this.setCurrentDay= this.setCurrentDay.bind(this);
      this.state = {
        extraDays: [],
        currentDays: [],
        prevDays: [],
        display: this.months[this.currentmonth]+' '+ this.currentyear,
      };
    }
    this.calendar();
  }
  /**
   *
   */
  calendar() {
    const numCurDays = this.daysInMonth(this.currentyear, this.currentmonth+1);
    const dayOfMonth = this.currentDate.getDate();
    this.currentDate.setDate(1);
    const dayOfweek = this.currentDate.getDay();
    const prevLastDay = new Date(this.currentDate.getFullYear(),
      this.currentDate.getMonth(), 0).getDate();
    let id = 0;
    let count = dayOfweek-1;
    // insert extra days before the month
    for (let i=0; i< dayOfweek; i++) {
      const daynum = prevLastDay-count;
      const node = React.createElement('div', {id: 'd'+id, key: 'd'+id,
        className: 'prevmonth'}, daynum);
      this.state.extraDays.push(node);
      id++;
      count--;
    }
    for (let i = 1; i <= numCurDays; i++) {
      const node = React.createElement('div', {id: 'd'+id, key: 'd'+id,
        className: 'currentmonth'}, i);
      this.state.currentDays.push(node);
      if (i===dayOfMonth) {
        const node1 = React.createElement('div', {id: 'today', key: 'd'+id,
          className: 'd'+id}, i);
        this.state.currentDays.pop(node);
        this.state.currentDays.push(node1);
      }
      id++;
    }
    for (let i = 1; i<(43-(numCurDays+dayOfweek)); i++) {
      const node = React.createElement('div', {id: 'd'+id, key: 'd'+id,
        className: 'nextmonth'}, i);
      this.state.prevDays.push(node);
      id++;
    }
  }
  /**
   *
   */
  setDate() {
    console.log('called');
    // const elem = document.getElementById('today');
    // if (elem) {
    //   elem.id = elem.className;
    //   elem.className = 'currentmonth';
    // }
    // // console.log(date);
    // const day = date.getDate();
    // // console.log(day);
    // const month = date.getMonth();
    // this.month = month;
    // const year = date.getFullYear();
    // this.year = year;
    // this.setState({display: this.months[month]+' '+year});
    // this.changedCurrentDate = date;
    // this.all();
    // const elements = document.getElementsByClassName('currentmonth');
    // if (!(document.getElementById('today'))) {
    //   for (const elem of elements) {
    //     if (elem.innerText == day) {
    //       elem.setAttribute('class', elem.id);
    //       elem.setAttribute('id', 'today');
    //     }
    //   }
    // }
  }
  /**
   *
   */
  returned() {
    this.setState({display: this.months[this.currentmonth]+
      ' '+ this.currentyear});
    const currentDate = new Date();
    this.month = currentDate.getMonth();
    this.year = currentDate.getFullYear();
    const dayOfMonth = currentDate.getDate();
    const numCurDays = this.daysInMonth(this.currentyear, this.currentmonth+1);
    currentDate.setDate(1);
    const dayOfweek = currentDate.getDay();
    const prevLastDay = new Date(currentDate.getFullYear(),
      currentDate.getMonth(), 0).getDate();
    let id = 0;
    let count = dayOfweek-1;
    if (document.getElementById('today')) {
      const elem = document.getElementById('today');
      elem.id = elem.getAttribute('class');
      elem.className ='currentmonth';
    }
    for (let i=0; i< dayOfweek; i++) {
      const node = document.getElementById('d'+id);
      node.innerHTML= prevLastDay-count;
      node.setAttribute('class', 'prevmonth');
      count--;
      id++;
    }
    for (let i = 1; i <= numCurDays; i++) {
      const node = document.getElementById('d'+id);
      node.innerHTML= i;
      node.setAttribute('class', 'currentmonth');
      if (node.innerText == dayOfMonth) {
        node.setAttribute('class', node.id);
        node.setAttribute('id', 'today');
      }
      id++;
    }
    for (let i = 1; i<(43-(numCurDays+dayOfweek)); i++) {
      const node = document.getElementById('d'+id);
      node.innerHTML= i;
      node.setAttribute('class', 'nextmonth');
      id++;
    }
    this.listeners();
  }
  /**
   *
   */
  next() {
    const elem = document.getElementById('today');
    if (elem) {
      elem.id = elem.className;
      elem.className = 'currentmonth';
    }
    this.month+=1;
    if (this.month > 11) {
      this.month = 0;
      this.year+=1;
    }
    this.setState({display: this.months[this.month]+' '+this.year});
    this.changedCurrentDate.setMonth(this.changedCurrentDate.getMonth()+1);
    this.all();
  }
  /**
   *
   */
  all() {
    let id = 0;
    const currentDate = new Date();
    const dayOfMonth = currentDate.getDate();
    const currentyear = currentDate.getFullYear();
    this.changedCurrentDate.setDate(1);
    const dayOfweek = this.changedCurrentDate.getDay();
    const prevLastDay = new Date(this.year,
      this.changedCurrentDate.getMonth(), 0).getDate();
    const numCurDays = this.daysInMonth(this.year, this.month+1);
    // get number of days in prev month
    let count = dayOfweek-1;
    for (let i=0; i< dayOfweek; i++) {
      const node = document.getElementById('d'+id);
      node.innerHTML= prevLastDay-count;
      node.setAttribute('class', 'prevmonth');
      count--;
      id++;
    }
    for (let i = 1; i <= numCurDays; i++) {
      const node = document.getElementById('d'+id);
      node.innerHTML= i;
      node.setAttribute('class', 'currentmonth');
      if ((this.month === this.currentmonth) &&
      (this.year === currentyear)) {
        if (node.innerText == dayOfMonth) {
          node.setAttribute('class', node.id);
          node.setAttribute('id', 'today');
        }
      }
      id++;
    }
    for (let i = 1; i<(43-(numCurDays+dayOfweek)); i++) {
      const node = document.getElementById('d'+id);
      node.innerHTML= i;
      node.setAttribute('class', 'nextmonth');
      id++;
    }
    this.listeners();
  }
  /**
   *
   */
  prev() {
    const elem = document.getElementById('today');
    if (elem) {
      elem.id = elem.className;
      elem.className = 'currentmonth';
    }
    this.month-=1;
    if (this.month < 0) {
      this.month = 11;
      this.year-=1;
    }
    this.setState({display: this.months[this.month]+' '+this.year});
    this.changedCurrentDate.setMonth(this.changedCurrentDate.getMonth()-1);
    this.changedCurrentDate.setDate(1);
    this.all();
  }
  /**
   *
   */
  listeners() {
    for (let i = 0; i<42; i++) {
      let node = document.getElementById('d'+i);
      if (!node) {
        node = document.getElementById('today');
      }
      node.removeEventListener('click', this.setCurrentDay);
      node.removeEventListener('click', this.setNext);
      node.removeEventListener('click', this.setPrev);
      if (node.className === 'nextmonth') {
        node.addEventListener('click', this.setNext);
      } else if (node.className === 'prevmonth') {
        node.addEventListener('click', this.setPrev);
      } else {
        node.addEventListener('click', this.setCurrentDay);
      }
    }
  }
  /**
   * @param {number} year
   * @param {number} month
   * @return {number}
  * return num of days in a month
  */
  daysInMonth(year, month) {
    // https://bobbyhadz.com/blog/javascript-get-number-of-days-in-month
    return new Date(year, month, 0).getDate();
  }
  /**
   * @param {number} event
   */
  setNext(event) {
    const eleminnext = document.getElementById('today');
    if (eleminnext) {
      eleminnext.setAttribute('id', eleminnext.getAttribute('class'));
      eleminnext.setAttribute('class', 'currentmonth');
    }
    const newid = event.currentTarget.getAttribute('id');
    const text = document.getElementById(newid).innerText;
    const elements = document.getElementsByClassName('currentmonth');
    this.next();
    if (!(document.getElementById('today'))) {
      for (const elem of elements) {
        if (elem.innerText === text) {
          elem.setAttribute('class', elem.id);
          elem.setAttribute('id', 'today');
        }
      }
    }
  }
  /**
   * @param {number} event
   */
  setPrev(event) {
    const eleminprev = document.getElementById('today');
    if (eleminprev) {
      eleminprev.setAttribute('id', eleminprev.getAttribute('class'));
      eleminprev.setAttribute('class', 'currentmonth');
    }
    const newid = event.currentTarget.getAttribute('id');
    const text = document.getElementById(newid).innerText;
    const elements = document.getElementsByClassName('currentmonth');
    this.prev();
    if (!(document.getElementById('today'))) {
      for (const elem of elements) {
        if (elem.innerText === text) {
          elem.setAttribute('class', elem.id);
          elem.setAttribute('id', 'today');
        }
      }
    }
  }
  /**
   * @param {number} event
   */
  setCurrentDay(event) {
    const elem = document.getElementById('today');
    const Id = event.currentTarget.getAttribute('id');
    const newelem = document.getElementById(Id);
    if ((elem) &&(!(newelem === elem))) {
      elem.setAttribute('id', elem.getAttribute('class'));
      elem.setAttribute('class', 'currentmonth');
      const newelem = document.getElementById(Id);
      newelem.setAttribute('class', newelem.id);
      newelem.setAttribute('id', 'today');
    } else {
      if (document.getElementById('today') == null) {
        const newelem = document.getElementById(Id);
        newelem.setAttribute('class', newelem.id);
        newelem.setAttribute('id', 'today');
      }
    }
  }
