import React from 'react';
import Picker from './Picker';
import './App.css';
// https://www.youtube.com/watch?v=I9L0ZQfSF1U&ab_channel=HarithaComputers%26Technology
/**
 * Simple component with no state.
 *
 * See the basic-react from lecture for an example of adding and
 * reacting to changes in state.
 */
class App extends React.Component {
  /**
   *
   */
  constructor() {
    super();
    this.state = {inputDate: ''};
    this.validate = this.validate.bind(this);
  }
  /**
   * @param {object} event
   */
  validate(event) {
    const button = document.getElementById('set');
    button.disabled = true;
    // if the input matches regex
    // make button clickble
    // save date into an object 
    this.setState({date: event.target.date});
    console.log(this.state.inputDate);
  }
  /**
   * @return {object} a <div> containing an <h2>
   */
  render() {
    const set = () =>{
      console.log('hi');
    };
    return (
      // can type anything into box
      // make set clikable only when text matches regex
      <div>
        <h2>CSE186 Assignment 4 - React I</h2>
        <Picker/>
        <input type ='text' id='date' date= {this.state.inputDate}
          onChange = {this.validate} placeholder="MM/DD/YYYY"
          disabled = {true}/>
        <button id = "set" onClick={set}>Set</button>
      </div>
    );
  }
}

export default App;
