import * as React from 'react';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import {Button} from '@mui/material';
import './RecipeModal.css';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};
/**
 *
 * See the basic-react example for an example of adding and reacting to
 * changes in state and lecture 10 for details on Material-UI
 *
 * @param {object} props
 * @return {object} JSX
 *
 */
export default function BasicModal(props) {
  console.log(props.recipesList);
  console.log(props.recipeViewed);
  console.log(props.recipeViewed.title);
  return (
    <div>
      <Modal
        open={props.visible}
        aria-labelledby='modal-modal-title'
        aria-describedby='modal-modal-description'
      >
        <Box sx={style}>
          <Button onClick={() => props.setRecipeVisible(false)}>x</Button>
          <div className='recipe'>
            <div>{props.recipeViewed.title}</div>
            <div>Instructions</div>
            {/* <span>{props.recipeViewed.extendedIngredients[0]}</span> */}
            {/* <div>{props.recipeViewed.analyzedInstructions[0].steps[0]}</div> */}
          </div>
        </Box>
      </Modal>
    </div>
  );
}
