import React from 'react';
import Picker from './Picker';
import './App.css';
// https://www.youtube.com/watch?v=I9L0ZQfSF1U&ab_channel=HarithaComputers%26Technology
/**
 * Simple component with no state.
 *
 * See the basic-react from lecture for an example of adding and
 * reacting to changes in state.
 */
class App extends React.Component {
  /**
   *
   */
  constructor() {
    super();
    this.state = {value: '', buttonState: true};
    this.validate = this.validate.bind(this);
    this.dateObj = {};
    this.child = React.createRef();
  }
  /**
   * @param {object} event
   */
  validate(event) {
    this.setState({value: event.target.value});
    const dateregex =
    /^(0?[1-9]|1[0-2])\/(0?[1-9]|1\d|2\d|3[01])\/(19|20)\d{2}$/;
    const dateStr = event.target.value;
    if (!(dateregex.test(dateStr))) {
      this.setState({buttonState: true});
    }
    // https://stackoverflow.com/questions/15196451/regular-expression-to-validate-datetime-format-mm-dd-yyyy
    if (dateregex.test(dateStr)) {
      // https://bobbyhadz.com/blog/javascript-convert-string-to-date-object#:~:
      // text=Use%20the%20Date()%20constructor,
      // and%20returns%20a%20Date%20object.&text=Copied!&text=We%20used%2
      // 0the%20Date(), string%20to%20a%20Date%20object.
      const [month, day, year] = dateStr.split('/');
      this.dateObj = new Date(+year, +month - 1, +day);
      this.setState({buttonState: false});
    }
  }
  /**
   *
   */
  set() {
    // console.log(this.dateObj);
    // const dateObj = this.dateObj;
    // console.log(dateObj);
    this.child.current.setDate();
  }
  /**
   * @return {object} a <div> containing an <h2>
   */
  render() {
    return (
      <div>
        <h2>CSE186 Assignment 4 - React I</h2>
        <Picker ref={this.child} />
        <input type ='text' id='date' value= {this.state.value}
          onChange = {this.validate} placeholder="MM/DD/YYYY"/>
        <button type = "submit" id = "set" onClick={this.set}
          disabled = {this.state.buttonState}>Set</button>
      </div>
    );
  }
}

export default App;
