import * as React from 'react';
import Appbar from './components/AppBar';
import SearchIcon from '@mui/icons-material/Search';
import {useState} from 'react';
import CloseIcon from '@mui/icons-material/Close';
/**
 *
 * establishes all the routes in the app
 *
 * @return {object}
 */
export default function Ingredients() {
  const [ingredients, setIngredients] = useState('');
  const getIngredients = (event) => {
    setIngredients(event.target.value);
    console.log(ingredients);
  };

  const onKeyUpValue = (event) => {
    console.log('User pressed: ', event.key);

    if (event.key === 'Enter') {
      // üëáÔ∏è your logic here
      console.log('Enter key pressed ‚úÖ');
    }
  };

  // on enter key check input with regex
  // if it passses
  //    make api call with ingredients
  // else
  // modal giving user right format

  return (
    <div className='Ingredients'>
      <Appbar></Appbar>
      <div className='search'>
        <div className='searchInputs'>
          <input
            type='text'
            value={ingredients}
            pattern='/^[a-zA-Z\s]+\,[a-zA-Z\s]+$/'
            placeholder='Ingredients'
            onChange={getIngredients}
            onKeyUp={onKeyUpValue}
          />
          <div className='searchIcon'>
            {ingredients === '' ? (
              <SearchIcon></SearchIcon>
            ) : (
              <CloseIcon></CloseIcon>
            )}
          </div>
          <div className='note'>*list ingredients separated by commas</div>
        </div>
      </div>
    </div>
  );
}
