import React from 'react';
import './Picker.css';
// https://reactjs.org/blog/2015/12/18/react-components
// -elements-and-instances.html#:~: text=Class%20components
// %20have%20instances%2C%20but,or%20an%20element%20factory%20helper.
/**
 * Picker component.
 */
class Picker extends React.Component {
  /**
   * Create a date picker
   * @param {string} containerId id of a node the Picker will be a child of
   */
  constructor(containerId) {
    super(containerId);
    if ((containerId) != null) {
      const currentDate = new Date();
      this.currentDate = currentDate;
      this.changedCurrentDate = currentDate;
      this.currentmonth = currentDate.getMonth();
      this.currentyear = currentDate.getFullYear();
      this.month = currentDate.getMonth();
      this.year = currentDate.getFullYear();
      this.months = ['January', 'February', 'March', 'April', 'May', 'June',
        'July', 'August', 'September', 'October', 'November', 'December'];
      this.calendar();
    }
  }
  /**
   *
   */
  calendar() {
    const numCurDays = this.daysInMonth(this.currentyear, this.currentmonth+1);
    console.log(numCurDays);
    const dayOfMonth = this.currentDate.getDate();
    console.log(dayOfMonth);
    this.currentDate.setDate(1);
    const dayOfweek = this.currentDate.getDay();
    console.log(dayOfweek);
    const prevLastDay = new Date(this.currentDate.getFullYear(),
      this.currentDate.getMonth(), 0).getDate();
    console.log(prevLastDay);
    // insert extra days before the month
    // for (let i=0; i< dayOfweek; i++) {
    //   const dayToAdd = document.createElement('div');
    //   dayToAdd.setAttribute('id', 'd'+id);
    //   id++;
    //   const textnode = document.createTextNode(prevLastDay-count);
    //   count--;
    //   dayToAdd.append(textnode);
    //   document.getElementById('nums').appendChild(dayToAdd);
    //   dayToAdd.setAttribute('class', 'prevmonth');
    //   dayToAdd.addEventListener('click', this.setPrev);
    // }
  }
  /**
   * @param {number} year
   * @param {number} month
   * @return {number}
  * return num of days in a month
  */
  daysInMonth(year, month) {
    // https://bobbyhadz.com/blog/javascript-get-number-of-days-in-month
    return new Date(year, month, 0).getDate();
  }
  /**
   * @return {object} a <div> containing the picker
   */
  render() {
    const extraDays = [1,2,3,4];
    return (
      <div id = "picker">
        <div className = "finaltable">
          <div className = "header">
            <button id = "prev" onClick={prev}>prev</button>
            <div id = "display" onClick={display}>
              {this.months[this.currentmonth]+' '+ this.currentyear}</div>
            <button id = "next" onClick={next}>next</button>
          </div>
          <div className = 'days'>
            <div className ='day0'>{'S'}</div>
            <div className ='day1'>{'M'}</div>
            <div className ='day2'>{'T'}</div>
            <div className ='day3'>{'W'}</div>
            <div className ='day4'>{'T'}</div>
            <div className ='day5'>{'F'}</div>
            <div className ='day6'>{'S'}</div>
          </div>
          <div id = 'nums'>{extraDays}</div>
        </div>
      </div>
    );
  }
}


const next = () =>{
  // calendar();
  console.log('next');
};
const prev = () =>{
  console.log('prev');
};
const display = () =>{
  console.log('display');
};
export default Picker;
