    <div className='recipe'>
      <div>{props.recipeViewed.title}</div>
      <div>Ingredients</div>
      {ingredients?.map((ingredient) => (
        <div>
          <span className='recipeContainer' key={ingredient.name}></span>
          <div> {ingredient.name}</div>
        </div>
      ))}
      {instructions?.map((instruction) => (
        <div>
          <span className='recipeContainer' key={instruction.name}></span>
          <div>Instructions</div>{' '}
          {instruction.steps?.map((step) => (
            <span className='recipeContainer' key={step.number}>
              <span> {step.number}</span>
              <span> {step.step}</span>
            </span>
          ))}
        </div>
      ))}
    </div>
  );
}
